<?php defined('BASEPATH') or exit('No direct script access allowed');

class Setup extends CI_Controller
{
    public function __construct()
    {
        parent::__construct();
        $this->load->helper('form');
        $this->load->helper('url');
        $this->load->library('form_validation');
    }

    public function index()
    {
        $data['add_css'] = array();
        $data['add_js'] = array();
        $data['user'] = false;
        $data["logged"] = false;

        $this->load->view('universal/header', $data);
        $this->load->view('admin/setup', $data);
        $this->load->view('universal/footer', $data);
    }

    public function install()
    {

        $data['add_css'] = array();
        $data['add_js'] = array();
        $data['user'] = false;
        $data["logged"] = false;
        $data["err_desc"] = '';

        define('INITSQL', base64_decode(""));

        $this->load->view('universal/header', $data);
        try {

            if (file_exists(APPPATH."/install.lock")) {
                throw new \Exception("您已运行过安装程序，如需重新安装，请删除数据库和 application 目录下的 install.lock 文件！");
            }

            $dbhost = $this->input->post('dbhost');
            $dbport = $this->input->post('dbport');
            $dbuser = $this->input->post('dbuser');
            $dbpass = $this->input->post('dbpass');
            $dbname = $this->input->post('dbname');

            $config['hostname'] = $dbhost;
            $config['port'] = $dbport;
            $config['username'] = $dbuser;
            $config['password'] = $dbpass;
            $config['database'] = '';
            $config['dbdriver'] = 'mysqli';
            $config['dbprefix'] = '';
            $config['pconnect'] = FALSE;
            $config['db_debug'] = TRUE;
            $config['cache_on'] = FALSE;
            $config['cachedir'] = '';
            $config['char_set'] = 'utf8';
            $config['dbcollat'] = 'utf8_general_ci';

            $tempDBC = $this->load->database($config, TRUE);

            if (!$tempDBC->query("CREATE DATABASE IF NOT EXISTS $dbname DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci;")){
                throw new \Exception('数据库创建失败！'. $_value);
            }
            $config['database'] = $dbname;
            $DBP = $this->load->database($config, TRUE);

            $_arr = explode(';', INITSQL);

            foreach ($_arr as $_value) {
                if (!empty($_value) && !$DBP->query($_value.';')){
                    throw new \Exception('数据表创建失败！'. $_value);
                }
            }

            $db_info = "<?php
            defined('BASEPATH') OR exit('No direct script access allowed');

            \$active_group = 'default';
            \$query_builder = TRUE;

            \$db['default'] = array(
            	'dsn'	=> '',
            	'hostname' => '$dbhost',
                'port' => '$dbport',
            	'username' => '$dbuser',
            	'password' => '$dbpass',
            	'database' => '$dbname',
            	'dbdriver' => 'mysqli',
            	'dbprefix' => '',
            	'pconnect' => FALSE,
            	'db_debug' => (ENVIRONMENT !== 'production'),
            	'cache_on' => FALSE,
            	'cachedir' => '',
            	'char_set' => 'utf8',
            	'dbcollat' => 'utf8_general_ci',
            	'swap_pre' => '',
            	'encrypt' => FALSE,
            	'compress' => FALSE,
            	'stricton' => FALSE,
            	'failover' => array(),
            	'save_queries' => TRUE
            );";

            if (!file_put_contents(APPPATH."config/database.php", $db_info)) {
                throw new \Exception("配置文件不可写，请手动复制以下信息到 application/config/database.php 文件并编辑。<br> $db_info ");
            }

            $this->load->database();
            $this->load->library('ion_auth');

            $mail_info = "<?php defined('BASEPATH') OR exit('No direct script access allowed');
            \$config['protocol'] = 'smtp';
            \$config['smtp_host'] = '".$this->input->post('mxhost')."';
            \$config['smtp_user'] = '".$this->input->post('mxuser')."';
            \$config['smtp_pass'] = '".$this->input->post('mxpass')."';
            \$config['smtp_port'] = '".$this->input->post('mxport')."';
            \$config['smtp_crypto'] = '".$this->input->post('mxsec')."';
            \$config['smtp_timeout'] = 15;
            \$config['mailtype'] = 'html';
            \$config['charset'] = 'utf-8';
            \$config['wordwrap'] = TRUE;
            \$config['newline'] = '\\r\\n';
            \$config['crlf'] = '\\r\\n';
";

            if (!file_put_contents(APPPATH."config/email.php", $mail_info)) {
                throw new \Exception("邮件配置文件不可写，请手动复制以下信息到 application/config/email.php 文件并编辑。<br> $mail_info ");
            }

            $ion_config = file_get_contents(APPPATH."config/ion_auth.php").PHP_EOL."\$config['admin_email'] = '".$this->input->post('mxname')."';";
            if (!file_put_contents(APPPATH."config/ion_auth.php", $ion_config)) {
                throw new \Exception("邮件配置文件不可写，请手动复制以下信息到 application/config/ion_auth.php 文件并覆盖。<br> $ion_config ");
            }


            $site_url = (isset($_SERVER['HTTPS']) ? "https" : "http") . "://$_SERVER[HTTP_HOST]/";


            $ci_config = file_get_contents(APPPATH."config/config.php").PHP_EOL."\$config['base_url'] = '$site_url';";
            if (!file_put_contents(APPPATH."config/config.php", $ci_config)) {
                throw new \Exception("系统配置文件不可写，请手动复制以下信息到 application/config/config.php 文件并覆盖。<br> $ci_config ");
            }

            $udata = array(
                'username' => "Administrator",
                'password' => password_hash($this->input->post('password'), PASSWORD_BCRYPT),
                'email' => $this->input->post('email'),
                'created_on' => time(),
                'active' => 1,
                'ip_address' => $_SERVER["REMOTE_ADDR"]
            );

            if (!$this->db->insert('users', $udata)){
                throw new \Exception("管理员用户创建失败，请检查数据库设置是否正确！");
            }

            if (!$this->db->insert('users_groups', array( 'user_id' => $this->db->insert_id(), 'group_id' => 1 ))){
                throw new \Exception("管理员用户创建失败，请检查数据库设置是否正确！");
            }

        } catch (\Exception $e) {
            $data["err_desc"] = $e->getMessage();
            $this->output->set_status_header('500');
            $this->load->view('StaticPage/install_err', $data);
            $this->load->view('universal/footer', $data);
            return false;
        }

        file_put_contents(APPPATH."/install.lock", time());

        $this->load->view('StaticPage/install_succ');
        $this->load->view('universal/footer', $data);

    }
}
